[BITS 16][ORG 0x7C00]start:    cli    xor ax, ax    mov ds, ax    mov ax, 0xB800    mov es, ax    xor di, di    mov ah, 0x17    mov al, ' '    mov cx, 2000.fill_loop:    stosw     loop .fill_loop    mov ah, 0x02    mov bh, 0x00    mov dh, 0x00    mov dl, 0x00    int 0x10hang:    jmp hangtimes 510 - ($ - $$) db 0dw 0xAA55